import axios from 'axios'
import {authAction} from '../userReducder'



export const login = (email,password)=>{
    return async dispatch=>{


        const config ={
            headers:{
                'Content-Type': 'application/json'
            }
        }
        try {

            const {data} = await axios.post(`/v1/api/amazona/users/login`,{email,password},config)

            console.log(data)
            dispatch(authAction.userLogin(data))


        }catch(err){
            console.log(err.response && err.response.data.message
                ? err.response.data.message
                : err.message)
            dispatch(authAction.userFail(err.response && err.response.data.message
                ? err.response.data.message
                : err.message))
        }

    }
}





export const register = (name,email,password)=>{
    return async dispatch=>{


        console.log(name,email,password)


        const config ={
            headers:{
                'Content-Type': 'application/json'
            }
        }
        try {

            const {data} = await axios.post(`/v1/api/amazona/users/user-reg`,{name,email,password},config)

            console.log(data)
            dispatch(authAction.userLogin(data))


        }catch(err){
            console.log(err.response && err.response.data.message
                ? err.response.data.message
                : err.message)
            dispatch(authAction.userFail(err.response && err.response.data.message
                ? err.response.data.message
                : err.message))
        }

    }
}
