import React,{Fragment,useEffect,useState} from 'react'
import {getproducts} from '../Store/Actions/ProductsActions'
import {useSelector,useDispatch} from 'react-redux'
import Products from './Products'
import Loader from './Loader'
import Pagination from 'react-js-pagination'

const Homepage = (props) => {
    console.log(props)
    const dispatch = useDispatch()

    const [currentPage,setCurrentPage] = useState(1)



    const products = useSelector(state => state.prod.products.products)
    const loading = useSelector(state => state.prod.loading)
    const err = useSelector(state => state.prod.er)
    const resPerPage = useSelector(state => state.prod.products.resPerPage)
    const productsCount = useSelector(state => state.prod.products.productsCount)


    useEffect(()=>{

        dispatch(getproducts())

    },[dispatch])


    const setCurrentPageNo =(no) =>{
        setCurrentPage(no)
    }




    return (
        <div classNameName='container container-fluid'>
            <h1 id="products_heading">Latest Products</h1>

            {/* {loading ? <Loader /> : err ? <div class="alert alert-danger" role="alert">
  {err.message}
</div> :(
<section id="products" className="container mt-5" >
  <div className="row">

            {products && products.map((product) => (
                <Products product={product} hello={props} id={product.id}/>
      ))} 
  </div>
</section>)}
            <div className="d-flex justify-content-center mt-5">
                <Pagination
                activePage={currentPage}
                itemsCountPerPage={resPerPage}
                totalItemsCount={productsCount}
                onChange={setCurrentPageNo}
                nextPageText={'Next'}
                prevPageText={'Previous'}
                firstPageText={'First'}
                lastPageText={'Last'}
                itemClass='page-item'
                linkClass='page-link'
                
                
                />

            </div> */}
        </div>
    )
}

export default Homepage
