import {configureStore,createSlice} from '@reduxjs/toolkit';

const products = localStorage.getItem('products') ?localStorage.getItem('products') : null

const productSlice = createSlice({
    name:'product',
    initialState:{products:null,loading:true,er:null},
    reducers:{
        allProductsRequest(state, action){
            state.products =[]
            state.loading =true
        },
        ProductsSuccess(state, action){
            state.products =action.payload
            state.loading =false
        },
        ProductsFail(state, action){
            state.er =action.payload
            state.loading =false
            state.products = []
        }

    }
})






const productDetailsSlice = createSlice({
    name:'product',
    initialState:{products:null,loading:true,er:null},
    reducers:{
        allProductsRequest(state, action){
            state.products =[]
            state.loading =true
        },
        ProductDetailsSuccess(state, action){
            state.products =action.payload
            state.loading =false
        },
        ProductsDetailsFail(state, action){
            state.er =action.payload
            state.loading =false
            state.products = []
        }

    }
})



export const productAction = productSlice.actions


const store = configureStore({
    reducer:{
        prod:productSlice.reducer
    }
})


export default store
