import {cartAction} from '../CartReducer'


export const addTocart =(data)=>{
    return (dispatch,getState) =>{


        dispatch(cartAction.addTocart(data))
        console.log(data)
        localStorage.setItem('cart', JSON.stringify(getState().cart.items))
    }
}
